function resizeIntroCover(){var n=$("#intro"),o=$("#intro_cover"),t=$(window).height();o.css("height",n.height()+.5*t)}function centerOnClick(){[["#intro_link","#intro"],["#about_link","#about"]].forEach(function(n){$(n[0]).click(function(o){o.preventDefault();var t=$(n[1]),i=Math.max($(window).height()-t.outerHeight(),0)/2,e=t.offset().top-i;$(window).scrollTop(e)})})}function adjust(){resizeIntroCover(),centerOnClick()}window.onload=adjust;